mapper.select:
SELECT MEMBERNO, MOVIENO, COMMENTNO, COMMENTCONTENTS, COMMENTCREATETIME, COMMENTUPDATETIME, COMMENTSTARRATING, MOVIETITLE, MEMBERNAME, MEMBERNICKNAME, MEMBERID FROM (SELECT /*+INDEX_DESC(MANAGER_MEMBER MANAGER_COMMENT_PK)*/ ROWNUM RN, MC.MEMBERNO, MC.MOVIENO, MC.COMMENTNO, MC.COMMENTCONTENTS, MC.COMMENTCREATETIME, MC.COMMENTUPDATETIME, MC.COMMENTSTARRATING, MMO.MOVIETITLE, MME.MEMBERNAME, MME.MEMBERNICKNAME, MME.MEMBERID FROM MANAGER_COMMENT MC, MANAGER_MEMBER MME, MANAGER_MOVIE MMO WHERE
MOVIETITLE LIKE '%'||#{keyword}||'%' OR MOVIEETITLE LIKE '%'||#{keyword}||'%'
MEMBERID=#{keyword}
MEMBERNICKNAME=#{keyword}
MC.MEMBERNO=MME.MEMBERNO AND MC.MOVIENO=MMO.MOVIENO AND ROWNUM <= (#{pageNum}*#{amount})) WHERE (RN > (#{pageNum}-1)*#{amount})
